// Şanlıurfa İlçe Haritası Verileri
// Gerçek GeoJSON verileri: geoBoundaries (HDX/OCHA) kaynaklı

// Kurum tipi — haritadaki pin stilini belirler
export type InstitutionType =
  | 'ISM'           // İl Sağlık Müdürlüğü — turkuaz bg, beyaz "İSM"
  | 'HASTANE'       // Kamu Hastanesi — mavi bg, beyaz "H"
  | 'OZEL'          // Özel Hastane — pembe bg, beyaz "H"
  | 'UNIVERSITE'    // Üniversite Hastanesi — mor bg, beyaz "H"
  | 'SEHIR'         // Şehir Hastanesi — kırmızı bg, beyaz "H" (büyük)
  | 'ILCE_SM'       // İlçe Sağlık Müdürlüğü — yeşil bg, beyaz "SM"
  | 'ADSH';         // ADSH/ADSM — turuncu bg, beyaz "A"

export interface InstitutionMarker {
  id: string;
  name: string;
  type: InstitutionType;
  lat: number;
  lng: number;
}

export interface DistrictData {
  name: string;
  center: [number, number]; // [lat, lng]
  svgLabelPos: [number, number]; // [x, y] in SVG coords
  color: string;
  hospitals: string[];
  ilceSM: string[];
  adsh: string[];
  population: number;
}

